mapper.select:
select
from IM_B_GOODS_INFO where 1=1
and NAME like CONCAT('%',#{goodsInfo.name},'%')
and TYPE = #{goodsInfo.type}
and CATEGORY = #{goodsInfo.category}
and STATUS = #{goodsInfo.status}
and UNIX_TIMESTAMP(IM_B_GOODS_INFO.STAR_NUMBER) >= UNIX_TIMESTAMP(STR_TO_DATE(#{starNumber1},'%Y-%m-%d %H-%i-%s'))
and UNIX_TIMESTAMP(IM_B_GOODS_INFO.STAR_NUMBER) <= UNIX_TIMESTAMP(STR_TO_DATE(#{starNumber2},'%Y-%m-%d %H-%i-%s'))
and UNIX_TIMESTAMP(IM_B_GOODS_INFO.CREATE_TIME) >= UNIX_TIMESTAMP(STR_TO_DATE(#{createTime1},'%Y-%m-%d %H-%i-%s'))
and UNIX_TIMESTAMP(IM_B_GOODS_INFO.CREATE_TIME) <= UNIX_TIMESTAMP(STR_TO_DATE(#{createTime2},'%Y-%m-%d %H-%i-%s'))
order by STATUS DESC limit #{page.dbIndex},#{page.dbNumber}

select count(ID) from IM_B_GOODS_INFO where 1=1
and NAME like CONCAT('%',#{goodsInfo.name},'%')
and TYPE = #{goodsInfo.type}
and CATEGORY = #{goodsInfo.category}
and STATUS = #{goodsInfo.status}
and UNIX_TIMESTAMP(IM_B_GOODS_INFO.STAR_NUMBER) >= UNIX_TIMESTAMP(STR_TO_DATE(#{starNumber1},'%Y-%m-%d %H-%i-%s'))
and UNIX_TIMESTAMP(IM_B_GOODS_INFO.STAR_NUMBER) <= UNIX_TIMESTAMP(STR_TO_DATE(#{starNumber2},'%Y-%m-%d %H-%i-%s'))
and UNIX_TIMESTAMP(IM_B_GOODS_INFO.CREATE_TIME) >= UNIX_TIMESTAMP(STR_TO_DATE(#{createTime1},'%Y-%m-%d %H-%i-%s'))
and UNIX_TIMESTAMP(IM_B_GOODS_INFO.CREATE_TIME) <= UNIX_TIMESTAMP(STR_TO_DATE(#{createTime2},'%Y-%m-%d %H-%i-%s'))

mapper.update:
update IM_B_GOODS_INFO
 STATUS = #{status,jdbcType=VARCHAR},
  UPDATE_TIME =#{updateTime,jdbcType=DATE}

where ID = #{id,jdbcType=BIGINT}
