mapper.update:
update IM_B_TASK_INFO
set  COUNT = #{count,jdbcType=BIGINT},
 AVAILABLE_STOCK = #{availableStock,jdbcType=BIGINT},
 UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP}
 where  ID = #{id,jdbcType=BIGINT}

mapper.select:
select count(ID)
from IM_B_TASK_INFO
where 1=1

  and NAME like CONCAT('%',#{taskInfo.name},'%')


  and TYPE = #{taskInfo.type}


  and CATEGORY = #{taskInfo.category}


  and STATUS = #{taskInfo.status}


  and STAR_LEVEL = #{taskInfo.starLevel}


  and IM_B_TASK_INFO.STAR_PROFIT >= #{starProfit1}


  and IM_B_TASK_INFO.STAR_PROFIT <= #{starProfit2}



  and UNIX_TIMESTAMP(IM_B_TASK_INFO.CREATE_TIME) >= UNIX_TIMESTAMP(STR_TO_DATE(#{createTime1},'%Y-%m-%d %H-%i-%s'))


  and UNIX_TIMESTAMP(IM_B_TASK_INFO.CREATE_TIME) <= UNIX_TIMESTAMP(STR_TO_DATE(#{createTime2},'%Y-%m-%d %H-%i-%s'))

select


from IM_B_TASK_INFO

where 1=1

  and NAME like CONCAT('%',#{taskInfo.name},'%')


  and TYPE = #{taskInfo.type}


  and CATEGORY = #{taskInfo.category}


  and STATUS = #{taskInfo.status}


  and STAR_LEVEL = #{taskInfo.starLevel}



  and IM_B_TASK_INFO.STAR_PROFIT >= #{starProfit1}


  and IM_B_TASK_INFO.STAR_PROFIT <= #{starProfit2}



  and UNIX_TIMESTAMP(IM_B_TASK_INFO.CREATE_TIME) >= UNIX_TIMESTAMP(STR_TO_DATE(#{createTime1},'%Y-%m-%d %H-%i-%s'))


  and UNIX_TIMESTAMP(IM_B_TASK_INFO.CREATE_TIME) <= UNIX_TIMESTAMP(STR_TO_DATE(#{createTime2},'%Y-%m-%d %H-%i-%s'))


order by STATUS DESC
limit
#{page.dbIndex},#{page.dbNumber}

select ,
from IM_B_TASK_INFO where 1=1
and STATUS = #{status}

mapper.update:
update IM_B_TASK_INFO
 STATUS = #{status,jdbcType=VARCHAR},
  UPDATE_TIME =#{updateTime,jdbcType=TIMESTAMP}

where ID = #{id,jdbcType=BIGINT}
