  TASK_SECOND_ID = #{taskSecondId,jdbcType=BIGINT},
  CREATE_TIME = #{createTime,jdbcType=DATE},
  UPDATE_TIME = #{updateTime,jdbcType=DATE}
where ID = #{id,jdbcType=BIGINT}

mapper.select:
